import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from "axios"

export const Add_user = () => {
  const [employees,setEmployees]=useState({

     name:"",
     designation:"",
     department:"",
     cover:""
  })
  const options=[
    {label:"Management",value:1},
    {label:"Technology",value:2},
    {label:"Finanace",value:3}
  ]
  const navigate=useNavigate()
const handleChange=(e)=>{
  setEmployees((prev)=>({...prev,[e.target.name]:e.target.value}))
}

const handleClick=async e=>{
  e.preventDefault()
  try{
    await axios.post("http://localhost:8800/users",employees)
    navigate("/")
  }
  catch(err){
    console.log(err)
  }
}
console.log(employees)
  return (
    <div>
      <div className="form">
        <h1>Add New Employee</h1>
        <input type="text" placeholder='name' onChange={handleChange}  name="name"/>
        <input type="text" placeholder='designation'  onChange={handleChange} name="designation"/>
        <select name='department' >
          {options.map(option=>(
            <option value={option.value}> {option.label} </option>
          ))}
        </select>
        <input type="text" placeholder='cover' onChange={handleChange}  name="cover"/>

      <button onClick={handleClick} >
    Add
      </button>

      </div>
    </div>
  )
}
